mapper.insert:
INSERT INTO
    tbl_product
    (
      category_code
    , prod_name
    , prod_price
    , soldout_yn
    , prod_desc
    )
VALUES
    (
      #{categoryCode}
    , #{prodName}
    , #{prodPrice}
    , #{soldOut}
    , #{prodDesc}
    )

INSERT INTO
tbl_prod_option
(prod_code, prod_size, prod_color, prod_option_soldout_yn)
VALUES

        (
          #{option.prodCode}
        , #{option.prodSize}
        , #{option.prodColor}
        , #{option.optionSoldOut}
        )

mapper.update:
UPDATE
    tbl_product
SET
      category_code = #{categoryCode}
    , prod_name = #{prodName}
    , prod_price = #{prodPrice}
    , soldout_yn = #{soldOut}
    , prod_desc = #{prodDesc}
WHERE
    prod_code = #{prodCode}

UPDATE
    tbl_prod_option
SET
      prod_size = #{prodSize}
    , prod_color = #{prodColor}
    , prod_option_soldout_yn = #{optionSoldOut}
WHERE
    prod_code = #{prodCode}
