mapper.insert:
INSERT INTO
    tbl_user
    (
      user_id
    , user_password
    , user_email
    , user_phone
    , user_address
    , user_type
    , user_name
    )
VALUES
    (
      #{userId}
    , #{password}
    , #{email}
    , #{phone}
    , #{address}
    , #{userType}
    , #{userName}
    )

mapper.update:
UPDATE
    tbl_user
SET
      user_id = #{userId}
    , user_password = #{password}
    , user_email = #{email}
    , user_phone = #{phone}
    , user_address = #{address}
    , user_type = #{userType}
    , user_name = #{userName}
WHERE
    user_code = #{userCode}
