mapper.insert:
INSERT INTO
    tbl_cart
    (
      prod_option_code
    , user_code
    , cart_quantity
    )
VALUES
    (
      #{prodOptionCode}
    , #{userCode}
    , #{cartQuantity}
    )

mapper.update:
UPDATE
    tbl_cart
SET
    cart_quantity = cart_quantity + #{cartQuantity}
WHERE
    prod_option_code = #{prodOptionCode}
AND user_code = #{userCode}

UPDATE
    tbl_cart
SET
    cart_quantity = #{cartQuantity}
WHERE
    prod_option_code = #{prodOptionCode}
AND user_code = #{userCode}

mapper.delete:
DELETE
    FROM tbl_cart
WHERE
    prod_option_code = #{prodOptionCode}
AND user_code = #{userCode}
